(grep -v '#' $1/ios/Podfile | grep -q 'use_frameworks' && echo "use_frameworks already exists in Podfile") || sed -i -e '/platform\ \:ios/a\'$'\n''use_frameworks!\'$'\n' $1/ios/Podfile ; sed -i -e '/flipper/ s/^#*/#/' $1/ios/Podfile
(grep -q "if target.name == \"VoxeetSDK\"" $1/ios/Podfile && echo "disable bitcode already exists in Podfile") || sed -i -e '/post_install\ \do\ \|installer\|/a\'$'\n''installer.pods_project.targets.each do |target|\'$'\n''if target.name == "VoxeetSDK" || target.name == "VoxeetUXKit" || target.name == "voxeet-uxkit-reactnative"\'$'\n''target.build_configurations.each do |config|\'$'\n''config.build_settings["ENABLE_BITCODE"] = "NO"\'$'\n''end end end\'$'\n' $1/ios/Podfile